    }
  }"]}}]}},{"type":"p","props":{"children":["This retrives breadcrumb data for a particular page during creation and can be passed into ",{"type":"code","props":{"children":["Layout"]}},", where it is used in a ",{"type":"code","props":{"children":["Breadcrumbs"]}}," component, which could be as simple as this:"]}},{"type":"pre","props":{"children":[{"type":"code","props":{"className":"language-javascript","children":["// src/components/Breadcrumbs.js

const Breadcrumbs = ({ data }) => (
  <nav className="breadcrumb-container">
    <ol className="breadcrumb">
      <li className="breadcrumb-item">
        <Link to="/">Home</Link>
      </li>
      {data.items.map(item => (
